package selenium_section36;

public class DoWhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 9;
		while(i>0) {
			System.out.println(i);
			i--;
		}
		
		System.out.println("**************");
		
		int j = 30;
		
		do {
			System.out.println(j);
			j++;
		} while (j<40);

	}

}
